import React from 'react';
import { createList, deleteItem} from '../actions';
import { connect } from 'react-redux';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {
      task: '', isEditing :false ,term :'' ,newkey:''
    }
  }

  handleChange(e) {
    this.setState({
      task: e.target.value
    })
  }
  handleSubmit(e) {
    e.preventDefault();
    if(this.state.task!==""){
      this.props.createList(this.state.task);
      this.setState({ task: '' });
    }
    else{
       alert("add task");
    }
  }

  deleteTerm(id){

    this.props.deleteItem(id);
  }

  editTerm(id,term){
    this.props.editItem(this.state.newkey ,this.state.term);
    this.setState({ term: '' ,isEditing:false });
  }



  listView (){
    return(
    this.props.contacts.map((contact,id) => {
      return (
          <div className="row" >
              {contact}
            <div className="col-md-2" key={id} style ={{textAlign :'center'}}>
               <button onClick={() => this.deleteTerm(id )} className="ui button primary">
                  Remove
                </button> 
                <button onClick={() => this.editTerm (id ,contact)} className="ui button negative">
                Edit
              </button> 
            </div>




          </div>
      );
    })
  ) 

  }

  render() {
    if(this.state.isEditing){
    return (
      <div className="container">
        <h1>TO Do List</h1>
        <hr />
        <div>
          <h3>Add Task</h3>

          <form onSubmit={this.handleSubmit} classNmae="ui form">
            <input type="text" className="form-control"
             onChange={this.handleChange}
             placeholder='enter the task'
             value={this.state.term} /><br />
            <input type="submit" className="btn btn-success" value="ADD" />
          </form>
        </div>
        <div className="ui container">
          {this.listView()}
        </div>
      </div>
    )
    }
    else {
      return(
        <div className="container">
        <h1>TO Do List</h1>
        <hr />
        <div>
          <h3>Add Task</h3>

          <form onSubmit={this.handleSubmit} classNmae="ui form">
            <input type="text" className="form-control"
             onChange={this.handleChange}
             placeholder='enter the task'
             value={this.state.name} /><br />
            <input type="submit" className="btn btn-success" value="ADD" />
          </form>
        </div>
        <div className="ui container">
          {this.listView()}
        </div>
      </div>
      )
    }
  }
}

const mapStateToProps = (state) => {
  return {
    contacts: state.contacts
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    createList: contact => dispatch(createList(contact)),
    deleteItem: index => dispatch(deleteItem(index)),
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
