import React from 'react';
import { createList, deleteItem } from '../actions';
import { connect } from 'react-redux';

class App extends React.Component {

  state = {
      task: ''
    }

  handleChange(e) {
    this.setState({
      task: e.target.value
    })
  }
  handleSubmit(e) {
    e.preventDefault();

    if(this.state.name !== ""){
      this.props.createList(this.state.task);
      this.setState({ task: '' });
    }
    else{
       alert("add task");
    }

  }

  deleteTerm(id){
    this.props.deleteItem(id);
  }

  editTerm(id){
    this.props.editItem(id,'priyanka');
  }

  listView (){
    return(
    this.props.contacts.map((contact,id) => {
      return (
          <div className="row" >
              {contact}
            <div className = "col-md-2" key={id} >
               <button onClick={() => this.deleteTerm(id)} className="btn btn-danger">
                  Remove
                </button>  
            </div>  
          </div> 
      )
    })
  ) 

  }


  render() {

    return (
      <div className="container">
        <h1>TO Do List</h1>
        <hr />
        <div>
          <h3>Add Task</h3>
          <form onSubmit={this.handleSubmit}>
            <input type="text" onChange={this.handleChange} className="form-control" value={this.state.name} /><br />
            <input type="submit" className="btn btn-success" value="ADD" />
          </form>
        </div>
        <div>
          {this.listView()}
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  //console.log(state.contacts,'state');
  return {
    contacts: state.contacts
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    createList: contact => dispatch(createList(contact)),
    deleteItem: index => dispatch(deleteItem(index))
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
