import streams from '../apis/streams';
import {SIGN_IN ,
        SIGN_OUT,
        CREATE_STREAM,
        FETCH_STREAMS,
        FETCH_STREAM,
        EDIT_STREAM,
        DELETE_STREAM


        } from './types';

//action creator 
export const  signIn = (userId) => {
    return {
        type : SIGN_IN,
        payload : userId
    };
};

export const signOut = () => {
    return {
        type : SIGN_OUT
    };

};
//creating streams through action creator
export const createStream = formValues => async (dispatch) => {
    const response = await streams.post('/streams', formValues);

    dispatch ({type: CREATE_STREAM ,payload :response.dat});
};
