import React from 'react';

class GoogleAuth extends React.Component {
   state={isSignedIn :null};

    //component render after this library load
    componentDidMount(){
        //wiring up google api library
        window.gapi.load('client : auth2' , () => {
            window.gapi.client.init({

                clientId : '1082789376786-5as1nfh108sh4rd2b4ph93scb4u6jq47.apps.googleusercontent.com',
                scope : 'email'
            }).then(() => {
                    //we get current authentication status
                    this.auth = window.gapi.auth2.getAuthInstance();
                    this.setState({ isSignedIn : this.auth.isignedIn.get() })

            });
        });
     }

     renderAuthButton() {

        if(this.state.isSignedIn === null) {

            return <div>I dont know if we are signed in</div>;
