export default (state = [], action ,update) => {
    switch (action.type){
      case "ADD_TODO":  
      return [...state, action.payload];
      case "DELETE_TASK":
        return state.filter((id) => id !== action.id);
      case "EDIT_TASK":
        return state.splice(action.payload,1,update);
      default:
        return state;
    }
  };
