import React from 'react';
import { createList, deleteItem} from '../actions';
import { connect } from 'react-redux';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {
      name: ''
    }
  }

  handleChange(e) {
    this.setState({
      name: e.target.value
    })
  }
  handleSubmit(e) {
    e.preventDefault();
    if(this.state.name!==""){
      this.props.createList(this.state.name);
      this.setState({ name: '' });
    }
    else{
       alert("add task");
    }
  }

  deleteTerm(id){

    this.props.deleteItem(id);
  }

  editTerm(id){
    this.props.editItem(id);

  }

renderList =() =>{

}


  listView (){
    return(
    this.props.contacts.map((contact,id) => {
      return (
          <div className="row" >
              {contact}
            <div className="col-md-2" key={id} >
               <button onClick={() => this.deleteTerm(id)} className="btn btn-danger">
                  Remove
                </button> 
            </div>

            <div className="col-md-2" key={id} >
              <button onClick={() => this.editTerm(id)} className="btn btn-danger">
                Edit
              </button> 
            </div>  
          </div>
      );
    })
  ) 

  }

  render() {

  }
}

const mapStateToProps = (state) => {
  return {
    contacts: state.contacts
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    createList: contact => dispatch(createList(contact)),
    deleteItem: index => dispatch(deleteItem(index)),
    //editItem:index => dispatch(editItem(index)
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
