import React from 'react';

class GoogleAuth extends React.Component {
    state = { isSignedIn: null };

    //component render after this library load
    componentDidMount() {
        // wiring up google api library
        window.gapi.load('client:auth2', () => {
            window.gapi.client
            .init({
                clientId: '531321390020-bn695k4i0628qbhdoeqk66k67as21ur1.apps.googleusercontent.com',
                scope: 'email'
            })
            .then(() => {
                //we get current authentication status
                this.auth = window.gapi.auth2.getAuthInstance();

                //if user is just signed in
                this.setState({ isSignedIn: this.auth.isSignedIn.get() });
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onAuthChange = () => {

        this.setState({isSignedIn : this.auth.isSignedIn.get() })
    };
    renderAuthButton() {

        if (this.state.isSignedIn === null) {
            return null;
        } else if (this.state.isSignedIn) {
            return (
                <button className ></button>
            );
        } else {
            return <div>I am not signed in</div>;
        }


    }
    render() {

        return (
            <div>
                {this.renderAuthButton()}
            </div>
        );
    }
}

export default GoogleAuth;
