import React from 'react';
import {createList ,deleteItem} from '../actions';
import {connect} from 'react-redux';

class App extends React.Component {

    constructor(props){
      super(props);
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);

      this.state = {
        name: ''
      }
    }

    handleChange(e){
        this.setState({
          name: e.target.value
        })
      }
    handleSubmit(e){
        e.preventDefault();
        this.props.createList(this.state.name);
        this.setState({name: ''});

      }

      listView(){
        console.log(this.props.contacts);
        this.props.contacts.map((contact)=>{
          return (
            <div className="row">
              <div className="col-md-10">
                {contact}
              </div>
              {/* {/* <div className="col-md-2">
                {/* <button onClick={(e) => this.deleteItem(e, index)} className="btn btn-danger">
                  Remove
                </button> */}
              </div>

          )})   


      }

      deleteItem(e, index){
        e.preventDefault();
        this.props.deleteItem(index);
      }

      render() {

        return(
          <div className="container">
            <h1>TO Do List</h1>
            <hr />
            <div>
              <h3>Add Task</h3>
              <form onSubmit={this.handleSubmit}>
                <input type="text" onChange={this.handleChange} className="form-control" value={this.state.name}/><br />
                <input type="submit" className="btn btn-success" value="ADD"/>
              </form>
              <hr />
            { <ul className="list-group">  
              {this.listView}

            </ul> }
            </div>
          </div>
        )
      }
    }

    const mapStateToProps = (state) => {
         return {
          contacts: state.contacts
        }
    };

    const mapDispatchToProps = (dispatch) => {
        return {
          createList: contact => dispatch(createList(contact)),
          deleteItem: index =>dispatch(deleteItem(index))
        }
      };

export default connect(mapStateToProps,mapDispatchToProps)(App);
