import React from 'react';
import {createList ,deleteItem} from '../actions';
import {connect} from 'react-redux';

class App extends React.Component {

    constructor(props){
      super(props);
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);

      this.state = {
        name: ''
      }
    }

    handleChange(e){
        this.setState({
          name: e.target.value
        })
      }
    handleSubmit(e){
        e.preventDefault();
        let contact = {
          name: this.state.name
        }
        this.setState({
          name: ''
        });
        this.props.createList(contact);
      }
      listView(data, index){
        return (
          <div className="row">
            <div className="col-md-10">
              <li key={index} className="list-group-item clearfix">
                {data.name}
              </li>
            </div>
            <div className="col-md-2">
              <button onClick={(e) => this.deleteItem(e, index)} className="btn btn-danger">
                Remove
              </button>
            </div>
        </div> 
        )
      }

      deleteItem(e, index){
        e.preventDefault();
        this.props.deleteItem(index);
      }

      render() {

        return(
          <div className="container">
            <h1>Clientside Contacts Application</h1>
            <hr />
            <div>
              <h3>Add Contact Form</h3>
              <form onSubmit={this.handleSubmit}>
                <input type="text" onChange={this.handleChange} className="form-control" value={this.state.name}/><br />
                <input type="submit" className="btn btn-success" value="ADD"/>
              </form>
              <hr />
            { <ul className="list-group">
              {this.props.contacts.map((task, i) => this.listView(task, i))}
            </ul> }
            </div>
          </div>
        )
      }
    }
    const mapStateToProps = (state, ownProps) => {
        return {
          contacts: state.contacts
        }
    };

    const mapDispatchToProps = (dispatch) => {
        return {
          createContact: contact => dispatch(createList(contact)),
          deleteContact: index =>dispatch(deleteItem(index))
        }
      };

export default connect(mapStateToProps,mapDispatchToProps)(App);
